import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { AddProduct, errorMessage } from 'src/app/Services/Guard/product';
import { AdminService } from 'src/app/Services/admin.service';
import { ToastrService } from 'ngx-toastr';
import { MatDialog } from '@angular/material/dialog';
import { UserService } from 'src/app/Services/user.service';
@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css'],
})
export class EditProductComponent implements OnInit {
  public product!: AddProduct;

  constructor(
    private adminService: AdminService,
    private toastr: ToastrService,
    private dialog: MatDialog,
    private userService:UserService,
  ) {}

ngOnInit() {
  const id = localStorage.getItem('id');

  this.adminService.getProductEdit(id).subscribe(
    (res: any) => {
      this.product = res;
    },
    (err) => {
      this.toastr.error(`${err.status} Error ${err.name}`);
    }
  );
}

  onSubmit(editProduct: NgForm) {
    const data = editProduct.value.id;
    this.product = editProduct.value;
    this.userService.editProduct(data, this.product).subscribe(
      () => {
        this.toastr.success('Product details edited successfully..!');
        this.dialog.closeAll();
      },
      (err: errorMessage) => {
        this.toastr.error(`${err.status} Error ${err.name}`);
      }
    );

    editProduct.resetForm();
  }
}
<div class="container">
  <form #editProduct="ngForm" (ngSubmit)="onSubmit(editProduct)">
    <h1>Edit product</h1>
    <div>
      <input
        id="id"
        name="id"
        class="formcontrol"
        type="text"
        placeholder="Enter the id"
        ngModel
        [(ngModel)]="product._id"
      />
      <input
        id="productName"
        name="productName"
        class="formcontrol"
        type="text"
        placeholder="Enter the name of the product"
        [(ngModel)]="product.productName"
      />
      <select
        id="productType"
        name="productType"
        for="productType"
        class="formcontrol"
        [(ngModel)]="product.productType"
        required
      >
        <option>Gram</option>
        <option>Kilogram</option>
        <option>Liter</option>
        <option>Count</option>
      </select>
      <input
        id="productPrice"
        name="productPrice"
        class="formcontrol"
        type="number"
        placeholder="Enter the price of the product"
        [(ngModel)]="product.productPrice"
      />
      <input
        id="productImage"
        name="productImage"
        class="formcontrol"
        type="text"
        placeholder="Enter the image path"
        [(ngModel)]="product.productImage"
      />
      <button>Edit Product</button>
    </div>
  </form>
</div>
