import { Component, OnInit } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { AddProduct, errorMessage } from 'src/app/Services/Guard/product';
import { UserService } from 'src/app/Services/user.service';
import { CartComponent } from '../cart/cart.component';

@Component({
  selector: 'app-trackDelivery',
  templateUrl: './trackDelivery.component.html',
  styleUrls: ['./trackDelivery.component.css'],
  providers:[CartComponent]
})
export class TrackDeliveryComponent implements OnInit {
  public cart: any = [];
  public uniqueCart: any;
  user = localStorage.getItem('Active-User');
  constructor(private userservice:UserService , private toastr:ToastrService) { }

  ngOnInit() {
   this.getCart()
  }
  getCart() {
    this.userservice.getCart(this.user,"Out for delivery").subscribe(
      (res: any) => {
        this.cart=res
        if (this.cart.length == 0) {
          localStorage.removeItem('id');
        }
      },
      (err: errorMessage) => {
        this.toastr.error(`${err.status} Error ${err.name}`);
      }
    );
    return this.cart;
  }

}
<app-navbar ></app-navbar>

<div *ngIf="cart.length != 0">
  <div>
    <h2>List of Products</h2>
    <table>
      <thead>
        <tr>
          <th>Product Image</th>
          <th>Product Name</th>
          <th>Product Type</th>
          <th>Product Price</th>
          <th>Delivery Status</th>
          <th>Quantity</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let product of cart">
          <td><img src="{{ product.productImage }}" /></td>
          <td>{{ product.productName }}</td>
          <td>{{ product.productType }}</td>
          <td>₹ {{ product.productPrice }}</td>
          <td></td>
          <td>{{product.quantity}}</td>
        </tr>
      </tbody>
      <tfoot>
        <tr>
          <th colspan="3">Total price of the products :</th>
          <td>₹ {{ (cart | sum : "total") }}</td>
        </tr>
      </tfoot>
    </table>
  </div>
</div>
<div *ngIf="cart.length == 0">
  <div class="card">
    <button id="button" routerLink="/home-path/view-home-path">
      Shop Now!
    </button>
  </div>
</div>
